	 	var truncate = 100;
	 	
	 	//Add spacing
	 	var desc = description.replace(/<li>/g, "<li> - ");
	     desc = desc.replace(/<br /g, " <br ");    
	     desc = desc.replace(/<p>/, " <p>");
	     
	     //add space after comma, removed by server OS class
	     desc = desc.replace(/,/g, ", ")
	 	
